package com.mbeddr.core.util.pluginSolution.plugin;

/*Generated by MPS */

import java.io.File;
import javax.swing.JTextField;
import jetbrains.mps.workbench.dialogs.project.newproject.PathField;

public class NewProjectWizardHelper {
  private static final String PROJECTS_DIR = System.getProperty("user.home") + File.separator + "MPSProjects";

  public static void updateProjectPath(JTextField myProjectName, PathField myProjectPath) {
    if (myProjectPath.getPath() == null || myProjectPath.getPath().length() == 0 || (myProjectPath.getPath().startsWith(PROJECTS_DIR) && !(myProjectPath.isPathChangedByUser()))) {
      myProjectPath.setPath(PROJECTS_DIR + File.separator + myProjectName.getText());
    }
  }

}
