package com.mbeddr.doc.editor;

/*Generated by MPS */

import java.util.List;
import java.io.File;
import jetbrains.mps.internal.collections.runtime.ListSequence;
import java.util.ArrayList;

public class FileHelper {

  public static List<String> allFilesNames(File dir) {
    List<String> res = ListSequence.fromList(new ArrayList<String>());
    collect(dir, dir.getAbsolutePath(), res);
    return res;
  }

  /*package*/ static void collect(File dir, String root, List<String> res) {
    for (File f : dir.listFiles()) {
      if (f.isDirectory()) {
        collect(f, root, res);
      } else {
        String p = f.getAbsolutePath();
        p = p.substring(root.length() + 1).replace('\\', '/');
        ListSequence.fromList(res).addElement(p);
      }
    }
  }

}
