package com.mbeddr.doc.editor;

/*Generated by MPS */

import javax.swing.JPanel;
import javax.swing.ImageIcon;
import java.awt.event.MouseAdapter;
import java.awt.event.MouseEvent;
import javax.swing.JComponent;
import java.awt.Dimension;
import java.awt.Graphics;
import java.awt.Graphics2D;
import java.awt.RenderingHints;
import java.awt.geom.AffineTransform;
import javax.swing.JFrame;

public class ImagePane extends JPanel {
  private ImageIcon imageIcon;
  private int MAX_WIDTH = 800;

  private String imagePath;
  private boolean presentationMode;
  private int width;
  private int height;

  public ImagePane(String imagePath, boolean presentationMode) {
    this.imagePath = imagePath;
    this.presentationMode = presentationMode;
    if (presentationMode) {
      this.addMouseListener(new MouseAdapter() {
        @Override
        public void mouseClicked(MouseEvent event) {
          if (event.getClickCount() == 2) {
            displayInFrame();
          }
        }
      });
    }
  }

  public JComponent loadImage() {
    // do not load the image with ImageIO or gifs don't work
    this.imageIcon = new ImageIcon(imagePath);
    float newWidth = (this.presentationMode ? Math.min(imageIcon.getIconWidth(), MAX_WIDTH) : imageIcon.getIconWidth());
    float newAspectRatio = imageIcon.getIconWidth() / newWidth;
    this.width = ((int) newWidth);
    this.height = (int) (imageIcon.getIconHeight() / newAspectRatio);
    return this;
  }

  @Override
  public Dimension getPreferredSize() {
    return new Dimension(this.width, this.height);
  }

  @Override
  protected void paintComponent(Graphics g) {
    super.paintComponent(g);
    Graphics2D g2d = (Graphics2D) g.create();
    g2d.setRenderingHint(RenderingHints.KEY_INTERPOLATION, RenderingHints.VALUE_INTERPOLATION_BILINEAR);
    g2d.setRenderingHint(RenderingHints.KEY_RENDERING, RenderingHints.VALUE_RENDER_QUALITY);
    g2d.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);

    int imageWidth = imageIcon.getIconWidth();
    int imageHeight = imageIcon.getIconHeight();
    if (imageWidth == 0 || imageHeight == 0) {
      return;
    }
    double widthScale = (double) getWidth() / (double) imageWidth;
    double heightScale = (double) getHeight() / (double) imageHeight;
    g2d.drawImage(imageIcon.getImage(), AffineTransform.getScaleInstance(widthScale, heightScale), this);
    g2d.dispose();
  }

  public void displayInFrame() {
    JFrame f = new JFrame();
    f.setSize(this.width, this.height);
    f.add(new ImagePane(this.imagePath, false).loadImage());
    f.setLocation(this.getLocationOnScreen());
    f.setVisible(true);
  }
}
