package com.mbeddr.core.base.editor;

/*Generated by MPS */

import java.util.StringTokenizer;

public class QNameShortener {
  public static String shorten(String qname) {
    if (qname == null) {
      return null;
    }
    StringTokenizer st = new StringTokenizer(qname, ".");
    StringBuilder res = new StringBuilder();
    while (st.hasMoreTokens()) {
      String t = st.nextToken();
      if (st.hasMoreTokens()) {
        res.append(t.charAt(0) + ".");
      } else {
        res.append(t);
      }
    }
    return res.toString();
  }
}
