package com.mbeddr.core.base.editor;

/*Generated by MPS */

import javax.swing.JLabel;
import org.jetbrains.mps.openapi.model.SNode;
import com.intellij.icons.AllIcons;
import javax.swing.Icon;

public abstract class UtilButton extends CommandButton {

  public JLabel createUpButton(final SNode n) {
    return createButtonWithIcon(n, AllIcons.General.ArrowUp);
  }

  public JLabel createDownButton(final SNode n) {
    return createButtonWithIcon(n, AllIcons.General.ArrowDown);
  }

  public JLabel createLeftButton(final SNode n) {
    return createButtonWithIcon(n, AllIcons.General.ArrowLeft);
  }

  public JLabel createRigthButton(final SNode n) {
    return createButtonWithIcon(n, AllIcons.General.ArrowRight);
  }

  protected JLabel createButtonWithIcon(final SNode n, Icon icon) {
    JLabel l = build(n, null);
    l.setIcon(icon);
    return l;
  }

  public abstract void perform(SNode n);
}
