import de.itemis.mps.editor.diagram.runtime.shape.AbstractShape;
import java.awt.Graphics2D;
import java.awt.geom.Rectangle2D;
import de.itemis.mps.editor.diagram.runtime.shape.IShapeStyle;
import java.awt.geom.GeneralPath;
import java.awt.Color;

public class Diamond extends AbstractShape {

  private boolean filled;
  private double factor;

  public Diamond(boolean filled, double factor) {
    this.filled = filled;
    this.factor = factor;
  }




  @Override
  protected void draw(final Graphics2D graphics, final Rectangle2D bounds, IShapeStyle style) {
    GeneralPath shape = new GeneralPath();
    double width = bounds.getWidth() * this.factor;
    double height = bounds.getHeight() * this.factor;
    double widthHalf = width / 2;
    double heightHalf = height / 2;
    double left = bounds.getX();
    double top = bounds.getY() - heightHalf / 2;
    double right = left + width;
    double bottom = top + height;
    double centerX = left + widthHalf;
    double centerY = top + heightHalf;
    shape.moveTo(centerX, top);
    shape.lineTo(right, centerY);
    shape.lineTo(centerX, bottom);
    shape.lineTo(left, centerY);
    shape.closePath();

    if (this.filled) {
      graphics.fill(shape);
    } else {
      Color color = graphics.getColor();
      graphics.setColor(Color.white);
      graphics.fill(shape);
      graphics.setColor(color);
      graphics.draw(shape);
    }
  }

}
