public class ExportEditorImage_Action extends BaseAction {
  private static final Icon ICON = null;

  public ExportEditorImage_Action() {
    super("Export Editor Image", "", ICON);
    this.setIsAlwaysVisible(false);
    this.setActionAccess(ActionAccess.UNDO_PROJECT);
    updateInBackground(true);
  }
  @Override
  public boolean isDumbAware() {
    return true;
  }
  @Override
  protected boolean collectActionData(AnActionEvent event, final Map<String, Object> _params) {
    if (!(super.collectActionData(event, _params))) {
      return false;
    }
    {
      EditorComponent editorComponent = event.getData(MPSEditorDataKeys.EDITOR_COMPONENT);
      if (editorComponent != null && editorComponent.isInvalid()) {
        editorComponent = null;
      }
      if (editorComponent == null) {
        return false;
      }
    }
    return true;
  }
  @Override
  public void doExecute(@NotNull final AnActionEvent event, final Map<String, Object> _params) {
    EditorCell cell = event.getData(MPSEditorDataKeys.EDITOR_COMPONENT).getRootCell();
    if (cell == null) {
      return;
    }
    SNode sNode = cell.getSNode();
    if (sNode == null) {
      return;
    }
    String name = sNode.getName();
    if (!(name == null || name.length() == 0)) {
      name = name.replaceAll("\\W+", "_");
    } else {
      name = sNode.getConcept().getName();
    }
    String imageFileName = SolutionUtil.createFilenameRelativeToNodeSolution(sNode, name + ".png");
    int width = cell.getWidth() + 15;
    int height = cell.getHeight() + 10;
    BufferedImage image = new BufferedImage(width, height, BufferedImage.TYPE_INT_RGB);
    Graphics2D g = image.createGraphics();
    g.setColor(Color.WHITE);
    g.fillRect(0, 0, width, height);
    event.getData(MPSEditorDataKeys.EDITOR_COMPONENT).paint(g);
    try {
      ImageTrimmer t = new ImageTrimmer();
      File file = new File(imageFileName);
      ImageIO.write(t.getCroppedImage(image, 0), "png", file);
      System.out.println("saved screenshot to " + file.getAbsolutePath());
    } catch (IOException ex) {
      ex.printStackTrace();
      ex.getCause().printStackTrace();
    }
  }
}
