package com.mbeddr.mpsutil.checkinHandler.plugin;

/*Generated by MPS */

import com.intellij.openapi.vcs.checkin.CheckinHandler;
import com.intellij.openapi.vcs.CheckinProjectPanel;
import com.intellij.openapi.project.Project;
import com.intellij.openapi.vcs.ui.RefreshableOnComponent;
import com.intellij.openapi.vcs.changes.CommitContext;
import javax.swing.JCheckBox;
import javax.swing.JComponent;
import javax.swing.JPanel;
import java.awt.GridLayout;
import com.intellij.ide.util.PropertiesComponent;
import org.jetbrains.annotations.Nullable;
import jetbrains.mps.project.MPSProject;
import jetbrains.mps.ide.project.ProjectHelper;
import java.util.List;
import org.jetbrains.mps.openapi.model.SModel;
import org.jetbrains.mps.openapi.module.SRepository;
import jetbrains.mps.smodel.SModelFileTracker;
import java.util.Collection;
import com.intellij.openapi.vfs.VirtualFile;
import jetbrains.mps.internal.collections.runtime.ListSequence;
import java.util.ArrayList;

public abstract class BaseCheckinHandler extends CheckinHandler {
  protected CheckinProjectPanel panel;
  protected Project ideaProject;
  protected String settingsKey;
  protected boolean perProjectSettings;
  protected String label;
  protected RefreshableOnComponent component;
  protected CommitContext context;

  public BaseCheckinHandler(final String settingsKey, final boolean perProjectSettings, String label) {
    this.settingsKey = settingsKey;
    this.perProjectSettings = perProjectSettings;
    this.label = label;
    final JCheckBox checkbox = new JCheckBox(label);
    component = new RefreshableOnComponent() {
      @Override
      public JComponent getComponent() {
        JPanel panel = new JPanel(new GridLayout(1, 0));
        panel.add(checkbox);
        return panel;
      }

      @Override
      public void refresh() {
      }

      @Override
      public void saveState() {
        if (perProjectSettings) {
          PropertiesComponent.getInstance(ideaProject).setValue(settingsKey, checkbox.isSelected());
        } else {
          PropertiesComponent.getInstance().setValue(settingsKey, checkbox.isSelected());
        }
      }

      @Override
      public void restoreState() {
        checkbox.setSelected(getKeyValue());
      }
    };
  }

  public BaseCheckinHandler initialize(CheckinProjectPanel panel, CommitContext context) {
    this.panel = panel;
    this.ideaProject = panel.getProject();
    this.context = context;
    return this;
  }

  public int getPriority() {
    return 1;
  }

  @Nullable
  @Override
  public RefreshableOnComponent getBeforeCheckinConfigurationPanel() {
    return this.component;
  }

  public boolean getKeyValue() {
    if (perProjectSettings) {
      return PropertiesComponent.getInstance(ideaProject).getBoolean(settingsKey);
    } else {
      return PropertiesComponent.getInstance().getBoolean(settingsKey);
    }
  }

  protected MPSProject getMPSProject() {
    return ProjectHelper.fromIdeaProject(ideaProject);
  }

  protected List<SModel> getCommitedModels() {
    MPSProject mpsProject = getMPSProject();

    final SRepository repository = mpsProject.getRepository();
    SModelFileTracker modelFileTracker = SModelFileTracker.getInstance(repository);
    Collection<VirtualFile> affectedFiles = panel.getVirtualFiles();
    // note, unlike getVirtualFiles, getFiles gives deleted files as well
    final List<SModel> affectedModels = ListSequence.fromList(new ArrayList<SModel>());
    for (VirtualFile file : affectedFiles) {
      SModel model = modelFileTracker.findModel(mpsProject.getFileSystem().fromVirtualFile(file));
      if (model == null) {
        continue;
      }
      ListSequence.fromList(affectedModels).addElement(model);
    }

    return affectedModels;
  }

  protected Collection<VirtualFile> getAffectedFiles() {
    return panel.getVirtualFiles();
  }

  protected boolean isDisposed() {
    return ideaProject.isDisposed();
  }
}
